

"""
matern52_kernel function created by kernel_coder(). Requires 1 hyperparameters. Likely created using matern52_kernel_base() as an input.
Use with include("src/kernels/matern52_kernel.jl").
hyperparameters == ["λ"]
"""
function matern52_kernel(
    hyperparameters::Vector{<:Real},
    δ::Real;
    dorder::Vector{<:Integer}=zeros(Int64, length(hyperparameters) + 2))

    @assert length(hyperparameters)==1 "hyperparameters is the wrong length"
    @assert length(dorder)==(1 + 2) "dorder is the wrong length"
    even_time_derivative = powers_of_negative_one(dorder[2])
    @assert maximum(dorder) < 3 "No more than two time derivatives for either t1 or t2 can be calculated"

    dorder[1] = sum(dorder[1:2])
    dorder[2:(end - 1)] = dorder[3:end]

    deleteat!(dorder, length(dorder))

    λ = hyperparameters[1]

    δ_positive = (δ >= 0)  # store original sign of δ (for correcting odd kernel derivatives)
    δ = abs(δ)  # this is corrected for later

    if dorder==[4, 2]
        func = 2000.0*exp(-2.23606797749979*δ/λ)/λ^6 - 2236.06797749979*exp(-2.23606797749979*δ/λ)*δ/λ^7 + 500.0*exp(-2.23606797749979*δ/λ)*δ^2/λ^8 + 500.0*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^6 - 536.65631459995*exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^5 - 200.0*exp(-2.23606797749979*δ/λ)*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^5 + 268.328157299975*exp(-2.23606797749979*δ/λ)*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^4 + 25.0*exp(-2.23606797749979*δ/λ)*(0.0 + 6.66666666666667*δ^2/λ^4 + 4.47213595499958*δ*(1 + 0.74535599249993*δ/λ)/λ^3)/λ^4 - 44.7213595499958*exp(-2.23606797749979*δ/λ)*(0.0 + 16.6666666666667*δ/λ^4 + 4.47213595499958*(1 + 0.74535599249993*δ/λ)/λ^3)/λ^3 - 559.016994374948*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^7 + 800.0*exp(-2.23606797749979*δ/λ)*δ*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^6 + 111.80339887499*exp(-2.23606797749979*δ/λ)*δ*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^6 - 200.0*exp(-2.23606797749979*δ/λ)*δ*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^5 + 125.0*exp(-2.23606797749979*δ/λ)*δ^2*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^8 - 223.606797749979*exp(-2.23606797749979*δ/λ)*δ^2*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^7
    end

    if dorder==[3, 2]
        func = -268.328157299975*exp(-2.23606797749979*δ/λ)/λ^5 + 400.0*exp(-2.23606797749979*δ/λ)*δ/λ^6 - 111.80339887499*exp(-2.23606797749979*δ/λ)*δ^2/λ^7 - 134.164078649987*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^5 + 90.0*exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^4 + 67.0820393249937*exp(-2.23606797749979*δ/λ)*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^4 - 60.0*exp(-2.23606797749979*δ/λ)*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^3 - 11.180339887499*exp(-2.23606797749979*δ/λ)*(0.0 + 6.66666666666667*δ^2/λ^4 + 4.47213595499958*δ*(1 + 0.74535599249993*δ/λ)/λ^3)/λ^3 + 15.0*exp(-2.23606797749979*δ/λ)*(0.0 + 16.6666666666667*δ/λ^4 + 4.47213595499958*(1 + 0.74535599249993*δ/λ)/λ^3)/λ^2 + 200.0*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^6 - 201.246117974981*exp(-2.23606797749979*δ/λ)*δ*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^5 - 50.0*exp(-2.23606797749979*δ/λ)*δ*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^5 + 67.0820393249937*exp(-2.23606797749979*δ/λ)*δ*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^4 - 55.9016994374948*exp(-2.23606797749979*δ/λ)*δ^2*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^7 + 75.0*exp(-2.23606797749979*δ/λ)*δ^2*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^6
    end

    if dorder==[2, 2]
        func = 20.0*exp(-2.23606797749979*δ/λ)/λ^4 - 44.7213595499958*exp(-2.23606797749979*δ/λ)*δ/λ^5 + 16.6666666666667*exp(-2.23606797749979*δ/λ)*δ^2/λ^6 + 30.0*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^4 - 8.94427190999916*exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^3 - 20.0*exp(-2.23606797749979*δ/λ)*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^3 + 8.94427190999916*exp(-2.23606797749979*δ/λ)*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^2 + 5.0*exp(-2.23606797749979*δ/λ)*(0.0 + 6.66666666666667*δ^2/λ^4 + 4.47213595499958*δ*(1 + 0.74535599249993*δ/λ)/λ^3)/λ^2 - 4.47213595499958*exp(-2.23606797749979*δ/λ)*(0.0 + 16.6666666666667*δ/λ^4 + 4.47213595499958*(1 + 0.74535599249993*δ/λ)/λ^3)/λ - 67.0820393249937*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^5 + 40.0*exp(-2.23606797749979*δ/λ)*δ*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^4 + 22.3606797749979*exp(-2.23606797749979*δ/λ)*δ*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^4 - 20.0*exp(-2.23606797749979*δ/λ)*δ*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^3 + 25.0*exp(-2.23606797749979*δ/λ)*δ^2*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^6 - 22.3606797749979*exp(-2.23606797749979*δ/λ)*δ^2*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^5
    end

    if dorder==[1, 2]
        func = exp(-2.23606797749979*δ/λ)*(0.0 + 16.6666666666667*δ/λ^4 + 4.47213595499958*(1 + 0.74535599249993*δ/λ)/λ^3) - 4.47213595499958*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^3 + 4.47213595499958*exp(-2.23606797749979*δ/λ)*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^2 - 2.23606797749979*exp(-2.23606797749979*δ/λ)*(0.0 + 6.66666666666667*δ^2/λ^4 + 4.47213595499958*δ*(1 + 0.74535599249993*δ/λ)/λ^3)/λ + 20.0*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^4 - 4.47213595499958*exp(-2.23606797749979*δ/λ)*δ*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^3 - 10.0*exp(-2.23606797749979*δ/λ)*δ*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^3 + 4.47213595499958*exp(-2.23606797749979*δ/λ)*δ*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^2 - 11.180339887499*exp(-2.23606797749979*δ/λ)*δ^2*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^5 + 5.0*exp(-2.23606797749979*δ/λ)*δ^2*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^4
    end

    if dorder==[0, 2]
        func = exp(-2.23606797749979*δ/λ)*(0.0 + 6.66666666666667*δ^2/λ^4 + 4.47213595499958*δ*(1 + 0.74535599249993*δ/λ)/λ^3) - 4.47213595499958*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^3 + 4.47213595499958*exp(-2.23606797749979*δ/λ)*δ*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^2 + 5.0*exp(-2.23606797749979*δ/λ)*δ^2*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^4
    end

    if dorder==[4, 1]
        func = -400.0*exp(-2.23606797749979*δ/λ)/λ^5 + 223.606797749979*exp(-2.23606797749979*δ/λ)*δ/λ^6 - 100.0*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^5 + 134.164078649987*exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^4 + 25.0*exp(-2.23606797749979*δ/λ)*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^4 - 44.7213595499958*exp(-2.23606797749979*δ/λ)*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^3 + 55.9016994374948*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^6 - 100.0*exp(-2.23606797749979*δ/λ)*δ*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^5
    end

    if dorder==[3, 1]
        func = 67.0820393249937*exp(-2.23606797749979*δ/λ)/λ^4 - 50.0*exp(-2.23606797749979*δ/λ)*δ/λ^5 + 33.5410196624969*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^4 - 30.0*exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^3 - 11.180339887499*exp(-2.23606797749979*δ/λ)*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^3 + 15.0*exp(-2.23606797749979*δ/λ)*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^2 - 25.0*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^5 + 33.5410196624969*exp(-2.23606797749979*δ/λ)*δ*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^4
    end

    if dorder==[2, 1]
        func = -6.66666666666667*exp(-2.23606797749979*δ/λ)/λ^3 + 7.4535599249993*exp(-2.23606797749979*δ/λ)*δ/λ^4 - 10.0*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^3 + 4.47213595499958*exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^2 + 5.0*exp(-2.23606797749979*δ/λ)*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ^2 - 4.47213595499958*exp(-2.23606797749979*δ/λ)*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2)/λ + 11.180339887499*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^4 - 10.0*exp(-2.23606797749979*δ/λ)*δ*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^3
    end

    if dorder==[1, 1]
        func = exp(-2.23606797749979*δ/λ)*(0.0 - 5.0*δ/λ^3 - 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ^2) + 2.23606797749979*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^2 - 2.23606797749979*exp(-2.23606797749979*δ/λ)*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2)/λ - 5.0*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^3 + 2.23606797749979*exp(-2.23606797749979*δ/λ)*δ*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^2
    end

    if dorder==[0, 1]
        func = exp(-2.23606797749979*δ/λ)*(0.0 - 1.66666666666667*δ^2/λ^3 - 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ^2) + 2.23606797749979*exp(-2.23606797749979*δ/λ)*δ*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^2
    end

    if dorder==[4, 0]
        func = 100.0*exp(-2.23606797749979*δ/λ)/λ^4 + 25.0*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^4 - 44.7213595499958*exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^3
    end

    if dorder==[3, 0]
        func = -22.3606797749979*exp(-2.23606797749979*δ/λ)/λ^3 - 11.180339887499*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^3 + 15.0*exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ^2
    end

    if dorder==[2, 0]
        func = 3.33333333333333*exp(-2.23606797749979*δ/λ)/λ^2 + 5.0*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ^2 - 4.47213595499958*exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ)/λ
    end

    if dorder==[1, 0]
        func = exp(-2.23606797749979*δ/λ)*(0.0 + 1.66666666666667*δ/λ^2 + 2.23606797749979*(1 + 0.74535599249993*δ/λ)/λ) - 2.23606797749979*exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)/λ
    end

    if dorder==[0, 0]
        func = exp(-2.23606797749979*δ/λ)*(1 + 2.23606797749979*δ*(1 + 0.74535599249993*δ/λ)/λ)
    end

    return -powers_of_negative_one(δ_positive || iseven(dorder[1])) * even_time_derivative * float(func)  # correcting for use of abs_δ and amount of t2 derivatives

end


return matern52_kernel, 1  # the function handle and the number of kernel hyperparameters
