    end

    if dorder==[0, 0, 0, 0]
        func = exp(-2*sin(pi*δ/qp_P)^2/p_λ^2 + (-1/2)*δ^2/se_λ^2)
    end

    return even_time_derivative * float(func)  # correcting for amount of t2 derivatives

end


return qp_periodic_kernel, 3  # the function handle and the number of kernel hyperparameters
