        func = 4*λ*(-2*λ*(-1 - α)*sin(δ*π/rq_P)^2/(α^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)) - log(1 + 2*λ*sin(δ*π/rq_P)^2/α))*π^2*sin(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-1 - α)/rq_P^2 - 4*λ*(-2*λ*(-1 - α)*sin(δ*π/rq_P)^2/(α^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)) - log(1 + 2*λ*sin(δ*π/rq_P)^2/α))*π^2*cos(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-1 - α)/rq_P^2 + 16*λ^2*π^2*sin(δ*π/rq_P)^2*cos(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-2 - α)/(α*rq_P^2) + 16*λ^2*(-1 - α)*π^2*sin(δ*π/rq_P)^2*cos(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-2 - α)/(α^2*rq_P^2) - 16*λ^2*(-1 - α)*(-2*λ*(-2 - α)*sin(δ*π/rq_P)^2/(α^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)) - log(1 + 2*λ*sin(δ*π/rq_P)^2/α))*π^2*sin(δ*π/rq_P)^2*cos(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-2 - α)/(α*rq_P^2)
    end

    if dorder==[1, 1, 0, 0]
        func = -4*λ*(-2*λ*(-1 - α)*sin(δ*π/rq_P)^2/(α^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)) - log(1 + 2*λ*sin(δ*π/rq_P)^2/α))*π*sin(δ*π/rq_P)*cos(δ*π/rq_P)*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-1 - α)/rq_P
    end

    if dorder==[0, 1, 0, 0]
        func = (2*λ*sin(δ*π/rq_P)^2/(α*(1 + 2*λ*sin(δ*π/rq_P)^2/α)) - log(1 + 2*λ*sin(δ*π/rq_P)^2/α))*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-α)
    end

    if dorder==[4, 0, 0, 0]
        func = -16*λ*π^4*sin(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-1 - α)/rq_P^4 + 16*λ*π^4*cos(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-1 - α)/rq_P^4 - 48*λ^2*(-1 - α)*π^4*sin(δ*π/rq_P)^4*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-2 - α)/(α*rq_P^4) - 48*λ^2*(-1 - α)*π^4*cos(δ*π/rq_P)^4*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-2 - α)/(α*rq_P^4) + 352*λ^2*(-1 - α)*π^4*sin(δ*π/rq_P)^2*cos(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-2 - α)/(α*rq_P^4) - 384*λ^3*(-2 - α)*(-1 - α)*π^4*sin(δ*π/rq_P)^2*cos(δ*π/rq_P)^4*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-3 - α)/(α^2*rq_P^4) + 384*λ^3*(-2 - α)*(-1 - α)*π^4*sin(δ*π/rq_P)^4*cos(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-3 - α)/(α^2*rq_P^4) - 256*λ^4*(-3 - α)*(-2 - α)*(-1 - α)*π^4*sin(δ*π/rq_P)^4*cos(δ*π/rq_P)^4*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-4 - α)/(α^3*rq_P^4)
    end

    if dorder==[3, 0, 0, 0]
        func = 16*λ*π^3*sin(δ*π/rq_P)*cos(δ*π/rq_P)*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-1 - α)/rq_P^3 - 48*λ^2*(-1 - α)*π^3*sin(δ*π/rq_P)*cos(δ*π/rq_P)^3*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-2 - α)/(α*rq_P^3) + 48*λ^2*(-1 - α)*π^3*sin(δ*π/rq_P)^3*cos(δ*π/rq_P)*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-2 - α)/(α*rq_P^3) - 64*λ^3*(-2 - α)*(-1 - α)*π^3*sin(δ*π/rq_P)^3*cos(δ*π/rq_P)^3*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-3 - α)/(α^2*rq_P^3)
    end

    if dorder==[2, 0, 0, 0]
        func = 4*λ*π^2*sin(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-1 - α)/rq_P^2 - 4*λ*π^2*cos(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-1 - α)/rq_P^2 - 16*λ^2*(-1 - α)*π^2*sin(δ*π/rq_P)^2*cos(δ*π/rq_P)^2*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-2 - α)/(α*rq_P^2)
    end

    if dorder==[1, 0, 0, 0]
        func = -4*λ*π*sin(δ*π/rq_P)*cos(δ*π/rq_P)*(1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-1 - α)/rq_P
    end

    if dorder==[0, 0, 0, 0]
        func = (1 + 2*λ*sin(δ*π/rq_P)^2/α)^(-α)
    end

    return even_time_derivative * float(func)  # correcting for amount of t2 derivatives

end


return rq_kernel, 3  # the function handle and the number of kernel hyperparameters
